mapper.sql:
select distinct dj.jdbc_code "jdbcCode", tds.sql_desc "sqlDesc", cd.code_name "codeName", tds.es_status "esStatus", tds.sql_id "id", tds.sql_code "sqlCode"
from T_DEF_SQL tds LEFT JOIN t_def_code_dict cd ON cd.code=tds.es_status left join t_def_sql_info ts on tds.sql_id=ts.sql_id left join t_def_bag_event be on be.sql_id=tds.sql_id left join t_def_monitor dm on dm.monitor_code=be.monitor_code left join t_def_jdbc dj on dj.id=tds.SQL_FROM_JDBC_ID where sub_buss_type='MONITOR_CONFIG' and cd.code_type='USER_STATUS'
and tds.es_status=#{esStatus,jdbcType=INTEGER}
and tds.sql_code like '%' || #{sqlCode,jdbcType=VARCHAR} || '%'
and tds.sql_desc like '%' || #{sqlDesc,jdbcType=VARCHAR} || '%'
and dj.jdbc_code=#{jdbcCode,jdbcType=VARCHAR}

mapper.select:
        select tAlias.* from
		(
	         select nAlias.*, rownum r_rownum from(

			 ) nAlias
		) tAlias where r_rownum >=#{queryStartIndex,jdbcType=INTEGER} and #{queryEndIndex,jdbcType=INTEGER} >= r_rownum

select sql_id "sqlId"
from t_def_sql
where sql_code=#{sqlCode,jdbcType=VARCHAR}
