mapper.sql:
SELECT RETURN_MSG, B.BATCH_NO, B.BUSS_TYPE, to_char(B.INSERT_TIME,'yyyy-mm-dd') as INSERT_TIME FROM T_DEF_ACTION_RETURN A, (SELECT DISTINCT BATCH_NO, BUSS_TYPE, MAX(action_date) AS INSERT_TIME, MAX(ACTION_NO) AS ACTION_NO FROM (SELECT DECODE(LENGTH(T.BATCH_NO), 20, T.BATCH_NO) AS BATCH_NO, T.action_date, R.BUSS_TYPE, R.ACTION_NO FROM T_DEF_ACTION_RETURN R, T_DEF_ACTION_TRACE T WHERE T.ACTION_NO = R.ACTION_NO AND LENGTH(T.BATCH_NO) = 20) GROUP BY BATCH_NO, BUSS_TYPE) B WHERE A.BUSS_TYPE = B.BUSS_TYPE AND A.ACTION_NO = B.ACTION_NO AND A.INSERT_TIME = B.INSERT_TIME
AND to_date(to_char(B.INSERT_TIME,'yyyy-mm-dd'),'yyyy-mm-dd')>=to_date(#{startDate,jdbcType=VARCHAR},'yyyy-mm-dd')
AND to_date(to_char(B.INSERT_TIME,'yyyy-mm-dd'),'yyyy-mm-dd')<=to_date(#{endDate,jdbcType=VARCHAR},'yyyy-mm-dd')
AND A.PLAT_COM =#{platCom,jdbcType=DECIMAL}
AND B.BUSS_TYPE =#{bussType,jdbcType=VARCHAR}

