mapper.sql:
SELECT BATCH_NO, BUSS_TYPE, TO_CHAR(ACTION_DATE, 'yyyy-mm-dd') AS ACTION_DATE, ACTION_MSG AS SUCCESS FROM T_DEF_ACTION_TRACE WHERE SUB_BUSS_TYPE = 'EB'
AND to_date(to_char(ACTION_DATE,'yyyy-mm-dd'),'yyyy-mm-dd')>=to_date(#{startDate,jdbcType=VARCHAR},'yyyy-mm-dd')
AND to_date(to_char(ACTION_DATE,'yyyy-mm-dd'),'yyyy-mm-dd')<=to_date(#{endDate,jdbcType=VARCHAR},'yyyy-mm-dd')
AND PLAT_COM =#{platCom,jdbcType=DECIMAL}
AND BUSS_TYPE =#{bussType,jdbcType=VARCHAR}

